const mainDataLuxury=()=>{const renderCarLuxury=arrayLuxury=>{const wrapperLuxury=document.getElementById("category-luxury");wrapperLuxury.innerHTML="",arrayLuxury.forEach(car=>{wrapperLuxury.insertAdjacentHTML("beforeend",`\n      <div class="car-item">\n        <div class="car-item-img">\n          <img src="${car.image}" width="390" height="250" alt="car 1" />\n        </div>\n        \x3c!-- /.car-item-img --\x3e\n        <div class="car-item-title">${car.name}</div>\n        \x3c!-- /.car-item-title --\x3e\n        <div class="car-item-info">\n          \n          <div class="car-item-point point">\n            <img src="img/transmission.png" width="35" height="35" alt="transmission" />\n            <p class="point-text"><span>Коробка</span> ${car.transmission}</p>\n          </div>\n          \x3c!-- /.car-item-point --\x3e\n\n          <div class="car-item-point point">\n            <img src="img/icons/gear.svg" width="35" height="35" alt="Gear" />\n            <p class="point-text"><span>Привод</span> ${car.gear}</p>\n          </div>\n          \x3c!-- /.car-item-point --\x3e\n\n          <div class="car-item-point">\n            <img src="img/piston.png" width="35" height="35" alt="Engine" />\n            <p class="point-text"><span>Двигатель</span> ${car.engine} л.с.</p>\n          </div>\n          \x3c!-- /.car-item-point --\x3e\n\n          \x3c!-- <div class="car-item-point">\n            <img src="img/icons/belt.svg" width="35" height="35" alt="Belt" />\n            <p class="point-text"><span>Количество</span> ${car.places} мест</p>\n          </div> --\x3e\n          \x3c!-- /.car-item-point --\x3e\n\n          <div class="car-item-point">\n            <img src="img/year.png" width="35" height="35" alt="year" />\n            <p class="point-text"><span>Випуск</span> ${car.year} год</p>\n          </div>\n          \x3c!-- /.car-item-point --\x3e\n      </div>\n      \x3c!-- /.car-item-info --\x3e\n\n      <div class="car-item-action">\n        <button class="car-item-btn button">Забронировать</button>\n      </div>\n      \x3c!-- /.car-item-btn --\x3e\n      `)})};fetch("https://luxorides-cars-default-rtdb.firebaseio.com/cars/1/luxury.json").then(response=>response.json()).then(data=>{renderCarLuxury(data)})};mainDataLuxury();