const paralax=()=>{document.addEventListener("DOMContentLoaded",(function(){let layer=document.querySelector(".price-image");document.addEventListener("mousemove",event=>{layer.style.transform="translate3d("+.9*event.clientX/15+"px,"+.5*event.clientY/5+"px,0px)"});const elem=document.querySelector(".basic");document.addEventListener("scroll",()=>{elem.style.backgroundPositionX="0"+.2*window.pageYOffset+"px"})}))};paralax();